<?phpnamespace WeatherData\includes;include_once '../vendor/autoload.php';include_once __DIR__ . '/navigation.php';function getHeader( $title, $alias = null , $project = 'WeatherData Application', $render = null ){    $header = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">    <html xmlns="http://www.w3.org/1999/xhtml">        <head>            <meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1" />            <!-- PHP Page title in here -->            <title>' . $title . '</title>            <!-- PHP Page title ends -->            <link rel="stylesheet" type="text/css" title="default" media="screen" href="css/layout.css" />            <link rel="stylesheet" type="text/css" title="default" media="screen" href="css/treatment.css" />            <link rel="stylesheet" type="text/css" media="print" title="default" href="css/print.css" />            <link rel="alternate stylesheet" title="large text" type="text/css" href="css/largetext.css" />            <link rel="alternate stylesheet" title="text only" type="text/css" href="css/textonly.css" />            <link rel="alternate stylesheet" title="high contrast" type="text/css" href="css/highcontrast.css" />            <!--[if gte IE 6]>            <link rel="stylesheet" type="text/css" href="css/iestyles.css" />            <![endif]-->            <!--[if lt IE 6]>            <link rel="stylesheet" type="text/css" href="css/ie5styles.css" />            <![endif]-->';    $header .= $render;//    if( $render !== null )//    {//        echo $render;//    }    $header .= '        </head>        <body>            <div id ="wrapper">                <div id="banner"><img src="images/home/furniture/logo.gif" alt="SAC" name="logo" width="60" height="65" id="logo" />                    <h1>' . $project . '</h1><!-- PHP: Page title goes in here -->                    <br class="clear"/>                </div>';    $header .= displayNavigation( $alias );    $header .= '<div id="content">                    <div id="page">';    return $header;}